$f = fopen("robot.jpg", "rb");
if(!$f){
	echo "Ошибка при открытии файла";
	exit();
}
$u = fread( $f, filesize("robot.jpg"));
fclose($f);

$r = mysql_connect();
mysql_select_db("datab", $r);

$query = "insert into tablefirst (picture) values ('$u')";
mysql_query($query);
mysql_close();
